 mkdir(folder_path3);
end

% Concatenate folder path, dataset name, and the desired suffix
filename1 = strcat(folder_path1, '21-zoo.mat', '.png');
filename2 = strcat(folder_path2, '21-zoo.mat', '.png');
filename3 = strcat(folder_path3, '21-zoo.mat', '.png');
% 
% Plot the convergence curve and save with custom filename
% colors = {
%     'r', [1, 0, 0];         % Red
%     'g', [0, 1, 0];         % Green
%     'b', [0, 0, 1];         % Blue
%     'c', [0, 1, 1];         % Cyan
%     'm', [1, 0, 1];         % Magenta
